import 'package:flutter/material.dart';class BackHandler extends StatelessWidget {  const BackHandler({super.key, required this.callBack});  final Function callBack;  @override  Widget build(BuildContext context) {    return SizedBox(      width: 0,      height: 0,      child: WillPopScope(        child: const SizedBox(),        onWillPop: () async {          await callBack();          return false;        },      ),    );  }}