import 'package:flutter/material.dart';class CustomIconButton extends StatefulWidget {  String text;  Icon icon;  Color backgroundColor;  VoidCallback? onClick;  CustomIconButton(      {super.key,      required this.text,      required this.icon,      required this.backgroundColor,        this.onClick});  @override  State<CustomIconButton> createState() => _CustomIconButtonState();}class _CustomIconButtonState extends State<CustomIconButton> {  @override  Widget build(BuildContext context) {    return ElevatedButton(      onPressed: widget.onClick,      style: ElevatedButton.styleFrom(        minimumSize: const Size(double.infinity, 50),        shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(12), // Rounded corners        ),        backgroundColor: widget.backgroundColor, // Button color        foregroundColor: Colors.white, // Text & icon color        elevation: 5, // Shadow effect        padding: const EdgeInsets.symmetric(horizontal: 16),      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.start,        children: [          widget.icon,          const SizedBox(width: 20),          Text(            widget.text,            style: const TextStyle(              fontSize: 16,              fontWeight: FontWeight.bold,            ),          ),        ],      ),    );  }}