import 'package:flutter/material.dart';class CustomTextField extends StatelessWidget {  final String label;  final TextInputType keyboardType;  final TextEditingController? controller;  final int? maxLength;  final String? error;  final bool readOnly;  final Function(String)? onChange;  const CustomTextField(      {super.key,      required this.label,      this.keyboardType = TextInputType.text,      this.controller,      this.maxLength,      this.error,        this.readOnly = false,      this.onChange});  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        TextField(          keyboardType: keyboardType,          controller: controller,          onChanged: onChange,          readOnly: readOnly,          maxLength: maxLength,          decoration: InputDecoration(            contentPadding: const EdgeInsets.all(8),            labelText: label,            errorText: error,            labelStyle: const TextStyle(fontSize: 16, color: Colors.grey),            border: const OutlineInputBorder(),            focusedBorder: const OutlineInputBorder(              borderSide: BorderSide(color: Colors.blue),            ),          ),        ),      ],    );  }}