import 'dart:async';import 'package:flutter/material.dart';class OTPResendTimer extends StatefulWidget {  const OTPResendTimer({super.key});  @override  _OTPResendTimerState createState() => _OTPResendTimerState();}class _OTPResendTimerState extends State<OTPResendTimer> {  Timer? _timer;  int _start = 30;  bool _isResendDisabled = true;  void startTimer() {    setState(() {      _start = 30;      _isResendDisabled = true;    });    _timer?.cancel(); // Cancel previous timer if any    _timer = Timer.periodic(Duration(seconds: 1), (timer) {      if (_start == 0) {        timer.cancel();        setState(() {          _isResendDisabled = false;        });      } else {        setState(() {          _start--;        });      }    });  }  @override  void initState() {    super.initState();    startTimer(); // Start the timer on screen load  }  @override  void dispose() {    _timer?.cancel();    super.dispose();  }  void resendOTP() {    // Your OTP resend logic here    print("Resending OTP...");    startTimer(); // Restart timer when OTP is resent  }  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.center,      children: [        Text(          _isResendDisabled ? "Resend in $_start sec" : "You can now resend OTP",          style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),        ),        SizedBox(height: 10),        ElevatedButton(          onPressed: _isResendDisabled ? null : resendOTP,          child: Text("Resend OTP"),        ),      ],    );  }}