import 'package:flutter/material.dart';class SubReportCard extends StatefulWidget {  final String title;  final String value;  final Color? valueColor ;  const SubReportCard({super.key, required this.title, required this.value,  this.valueColor =  Colors.blueAccent});  @override  State<SubReportCard> createState() => _SubReportCardState();}class _SubReportCardState extends State<SubReportCard> {  @override  Widget build(BuildContext context) {    return SizedBox(      width: double.infinity,      child: Row(        children: [          Expanded(            flex: 4,            child: Text(              widget.title,              style:             const TextStyle(                fontWeight:                FontWeight                    .w600,                fontSize: 12,               color: Colors.blueAccent              ),            ),          ),          const SizedBox(            width: 4,          ),          const Text(":"),          const SizedBox(            width: 12,          ),          Expanded(              flex: 6,              child: Text(                widget.value,                style:                  TextStyle(                  fontWeight:                  FontWeight                      .w800,                  fontSize:                  12,                    color: widget.valueColor                ),              )),        ],      ),    );  }}