import 'package:flutter/material.dart';import 'package:get/get.dart';extension GetSnackbarExtension on GetInterface {  void showSnackBar({    String? title,    required String message,    Duration? duration,    Color? backgroundColor,  }) {    showSnackbar(      GetSnackBar(        title: title,        message: message,        duration: duration ?? const Duration(seconds: 2),        backgroundColor: backgroundColor ?? Colors.black87,      ),    );  }}