import 'package:flutter/material.dart';import 'package:tcs_demo/utils/BioMetricAuth.dart';class BioMetricPage extends StatefulWidget {  const BioMetricPage({super.key});  @override  State<BioMetricPage> createState() => _BioMetricPageState();}class _BioMetricPageState extends State<BioMetricPage> {  bool isBioMetricEnabled = false;  @override  Widget build(BuildContext context) {    return Scaffold(        appBar: AppBar(          backgroundColor: Colors.blueAccent,          title: const Text(            'BioMetric',            style: TextStyle(                color: Colors.white, fontWeight: FontWeight.w600, fontSize: 16),          ),          leading: IconButton(            icon: const Icon(Icons.arrow_back, color: Colors.white),            onPressed: () {              Navigator.pop(context);            },          ),        ),        body: Stack(          children: [            Container(              decoration: const BoxDecoration(                image: DecorationImage(                  image: AssetImage('assets/images/inner1.png'),                  fit: BoxFit.cover,                ),              ),            ),            Column(              children: [                const SizedBox(height: 20),                Container(                  decoration: const BoxDecoration(                      color: Colors.white,                      borderRadius: BorderRadius.all(Radius.circular(4))),                  margin: const EdgeInsets.all(12),                  padding: const EdgeInsets.all(6),                  child: Row(                    mainAxisAlignment: MainAxisAlignment.spaceBetween,                    children: [                      const Row(                        children: [                          Icon(Icons.fingerprint),                          Text("Enable Biometric")                        ],                      ),                      Switch.adaptive(                          value: isBioMetricEnabled,                          onChanged: (value) async {                            if (value) {                              final hasBioMetricCompleted =                                  await BiometricAuth().authenticateUser();                              setState(() {                                isBioMetricEnabled = hasBioMetricCompleted;                              });                            }                          })                    ],                  ),                ),              ],            )          ],        ));  }}