import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:tcs_demo/viewModels/AuthViewModel.dart';import '../responseModels/EmployeeDetailResponse.dart';class EmployeeProfilePage extends StatefulWidget {  const EmployeeProfilePage({super.key});  @override  State<EmployeeProfilePage> createState() => _EmployeeProfilePageState();}class _EmployeeProfilePageState extends State<EmployeeProfilePage> {  final authViewModel = Get.put(AuthViewModel());  @override  Widget build(BuildContext context) {    return SafeArea(        top: true,        child: Scaffold(          appBar: AppBar(            backgroundColor: Colors.blueAccent,            title: const Text(              'Employee Profile',              style: TextStyle(                  color: Colors.white, fontWeight: FontWeight.w600, fontSize: 16),            ),            leading: IconButton(              icon: const Icon(Icons.arrow_back, color: Colors.white),              onPressed: () {                Navigator.pop(context);              },            ),          ),          body: Obx(() {            final employeeData = authViewModel.employeeDetailResponseState.value                .whenOrNull(success: (data) => data as EmployeeDetailResponse?)                ?.employeeDetails;            return Stack(              children: [                Container(                  decoration: const BoxDecoration(                    image: DecorationImage(                      image: AssetImage('assets/images/inner1.png'),                      fit: BoxFit.cover,                    ),                  ),                ),                Padding(                    padding: const EdgeInsets.all(20),                    child: Container(                      height: 360,                      decoration: BoxDecoration(                        color: Colors.white,                        borderRadius: BorderRadius.circular(12),                        boxShadow: [                          BoxShadow(                            color: Colors.black.withOpacity(0.1), // Light shadow                            blurRadius: 6,                            spreadRadius: 2,                            offset: Offset(2, 4), // Soft offset                          ),                        ],                      ),                      padding: EdgeInsets.all(12),                      child: Column(                        children: [                          buildProfileItem("Employee Name",                              employeeData?.employeeName ?? ""),                          buildProfileItem(                              "Login Id", employeeData?.loginId ?? ""),                          buildProfileItem(                              "User Role", employeeData?.designation ?? ""),                          buildProfileItem("Mobile Number",                              employeeData?.mobileNumber ?? ""),                          buildProfileItem("Email Id", ""),                        ],                      ),                    ))              ],            );          }),        ));  }  Widget buildProfileItem(String title, String value) {    return Column(      mainAxisAlignment: MainAxisAlignment.start,      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(title, style: const TextStyle(fontWeight: FontWeight.w500),),        const SizedBox(height: 4),        Text(          value,          style: const TextStyle(color: Colors.blue,fontWeight: FontWeight.w700,),        ),        Container(            height: 1,            width: double.infinity,            color: Colors.grey,            margin: const EdgeInsets.symmetric(vertical: 8)),        const SizedBox(height: 6),      ],    );  }}