import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:tcs_demo/viewModels/AuthViewModel.dart';class LoginPage extends StatefulWidget {  const LoginPage({super.key});  @override  State<LoginPage> createState() => _LoginPageState();}class _LoginPageState extends State<LoginPage> {  final authViewModel = Get.put(AuthViewModel());  final TextEditingController _controller = TextEditingController();  @override  Widget build(BuildContext context) {    return SafeArea(        top: true,        child: Scaffold(          resizeToAvoidBottomInset: false,          body: Stack(            children: [              Container(                decoration: const BoxDecoration(                  image: DecorationImage(                    image: AssetImage('assets/images/ccms_bg.png'),                    fit: BoxFit.fill,                  ),                ),              ),              Align(                alignment: Alignment.center,                child: Padding(                  padding: const EdgeInsets.all(20),                  child: Container(                    height: 220,                    decoration: BoxDecoration(                        borderRadius:                        const BorderRadius.all(Radius.circular(24)),                        border: Border.all(width: 2, color: Colors.blue)),                    child: Padding(                      padding: const EdgeInsets.all(24.0),                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          TextField(                            controller: _controller,                            keyboardType: TextInputType.number,                            decoration: InputDecoration(                              labelText: 'Employee ID / Mobile Number',                              filled: true,                              fillColor: Colors.grey[100],                              border: OutlineInputBorder(                                borderRadius: BorderRadius.circular(8),                                borderSide: BorderSide.none,                              ),                              focusedBorder: OutlineInputBorder(                                borderRadius: BorderRadius.circular(8),                                borderSide: const BorderSide(                                  color: Colors.blue,                                  width:                                  2.0, // More prominent border when focused                                ),                              ),                              enabledBorder: OutlineInputBorder(                                borderRadius: BorderRadius.circular(8),                                borderSide: BorderSide(                                  color: Colors.grey.shade400,                                  // Soft gray border when not focused                                  width: 1.5,                                ),                              ), // Adds an icon                            ),                          ),                          const SizedBox(height: 12),                          Obx(() =>                              authViewModel                                  .employeeDetailResponseState.value                                  .maybeWhen(                                  loading: () =>                                  const CircularProgressIndicator(                                    color: Colors.blueAccent,                                  ),                                  orElse: () =>                                      FilledButton(                                          onPressed: () {                                            FocusScope.of(context).unfocus();                                            authViewModel.fetchEmployeeDetails(                                                _controller.text);                                          },                                          style: FilledButton.styleFrom(                                            backgroundColor: Colors.blue,                                            shape: RoundedRectangleBorder(                                              borderRadius: BorderRadius                                                  .circular(                                                  8), // Set the border radius here (smaller value)                                            ),                                            padding: const EdgeInsets.symmetric(                                                vertical:                                                8), // Adjust padding for height                                          ),                                          child: const SizedBox(                                              width: double.infinity,                                              child: Text(                                                'Verify',                                                textAlign: TextAlign.center,                                              )))))                        ],                      ),                    ),                  ),                ),              )            ],          ),        ));  }}