import 'dart:convert';import 'package:crypto/crypto.dart';String sha512GEN(String text) {  var bytes = utf8.encode(text); // Convert string to UTF-8 bytes  var digest = sha512.convert(bytes); // Compute SHA-512 hash  return digest.toString(); // Convert hash to hex string}void main() {  String text = "T688148|1708068696287|1||c964634||||||||||||5316792357LHKNYF";  print(sha512GEN(text)); // Example usage}