import 'package:freezed_annotation/freezed_annotation.dart';part 'EmployeeDetailRequest.freezed.dart';part 'EmployeeDetailRequest.g.dart';@Freezed()class EmployeeDetailRequest with _$EmployeeDetailRequest {  const factory EmployeeDetailRequest(      {@JsonKey(name: 'LoginID') required String loginId }) =  _EmployeeDetailRequest;  factory EmployeeDetailRequest.fromJson(Map<String, dynamic> json) =>      _$EmployeeDetailRequestFromJson(json);}@Freezed()class VerifyOtpRequest with _$VerifyOtpRequest {  const factory VerifyOtpRequest(      {@JsonKey(name: 'LoginID') required String loginId,      @JsonKey(name: 'SentOTP') required String sentOtp,      @JsonKey(name: 'MPIN') required String mpin}) =  _VerifyOtpRequest;  factory VerifyOtpRequest.fromJson(Map<String, dynamic> json) =>      _$VerifyOtpRequestFromJson(json);}@Freezed()class MPinRequest with _$MPinRequest {  const factory MPinRequest(      {@JsonKey(name: 'LoginID') required String loginId,        @JsonKey(name: 'MPIN') required String mpin}) =  _MPinRequest;  factory MPinRequest.fromJson(Map<String, dynamic> json) =>      _$MPinRequestFromJson(json);}