import 'package:local_auth/local_auth.dart';class BiometricAuth {  final LocalAuthentication auth = LocalAuthentication();  Future<bool> authenticateUser() async {    try {      // Check if biometric authentication is available      bool canCheckBiometrics = await auth.canCheckBiometrics;      bool isDeviceSupported = await auth.isDeviceSupported();      if (!canCheckBiometrics || !isDeviceSupported) {        print("Biometric authentication is not available");        return false;      }      // Authenticate with biometrics      bool isAuthenticated = await auth.authenticate(        localizedReason: 'Scan your fingerprint or face to authenticate',        options: const AuthenticationOptions(          biometricOnly: true,          stickyAuth: true, // Keeps the dialog open until user cancels        ),      );      return isAuthenticated;    } catch (e) {      print("Error during authentication: $e");      return false;    }  }}