import 'package:flutter/material.dart';  Future<bool?> showCustomDialog(BuildContext context, String title, String content) {    return showDialog(      context: context,      barrierDismissible: false, // Prevent closing by tapping outside      builder: (BuildContext context) {        return AlertDialog(          shape: RoundedRectangleBorder(              borderRadius: BorderRadius.circular(8)),          title: Text(title, style: const TextStyle(fontWeight: FontWeight.bold)),          content: Text(content,style: const TextStyle(fontWeight: FontWeight.w500),),          actions: [            TextButton(              onPressed: () => Navigator.pop(context, false),              child: const Text("No", ),            ),            TextButton(              onPressed: () => Navigator.pop(context, true),              child: const Text("Yes" ),            ),          ],        );      },    );  }